    private static ArrayList<String> sample_themes = new ArrayList<>();

    public static void generateSampleThemesList(int count_themes) {
        if (count_themes > list.size()) count_themes = list.size();
        sample_themes.clear();
        int i = 0;
        while (i < count_themes) {
            String word = list.get((int) (Math.random() * (list.size()-1)) + 1);
            if (!sample_themes.contains(word)) sample_themes.add(word);
            i++;
        }
    }

    public static ArrayList<String> getSampleThemes() {
        return sample_themes;
    }
